import { Component, OnInit } from '@angular/core';
import { FormGroup } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{
  loginForm!: FormGroup({
    email: new FormControl('',[Validators.required,Validators.email]),
    passsword: new FormControl('',Validators.required)
  });
}
onsubmit(){
  if(this.loginForm.valid){
    console.log(this.loginForm.value);
  }
}
<html>
<head> <title> Login to MVP Infotec Pvt Ltd.</title></head>
<body><form [formGroup]="loginForm" (ngSumit)="onSubmit()">
    <div class="login" align="center">
    <div><h2>MVP INFOTECH PVT. LTD.</h2>
    <div>
    <p><label for="email">Email ID :</label>  <input type ="email" id="email" formControlName="email" class="box" placeholder="Email"></p>
    <div *ngIf="loginForm.get('email')?.invalid && loginForm.get('email')?.touched">
        <div *ngIf="loginForm.get('email')?.errors?.['required']">Email is required</div>
        <div *ngIf="loginForm.get('email')?.errors?.['email']">Invalid email format</div>
    </div>
    <p><label for="password">Password :</label> <input type="password" id="password" formControlName="password" class="box" placeholder="Password"></p>
    </div>
    </div>
    </div>
    <div><input id="submit" type="submit" value="SUBMIT"></div>
    </form>
</body>
</html>
