import { Component, OnInit } from '@angular/core';
import { FormGroup } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{
  loginForm!: FormGroup({
    email: new FormControl('',[Validators.required,Validators.email]),
    passsword: new FormControl('',Validators.required)
  });
}
onsubmit(){
  if(this.loginForm.valid){
    console.log(this.loginForm.value);
  }
}
<html>
<head> <title> Login to MVP Infotec Pvt Ltd.</title></head>
<body><form [formGroup]="loginForm" (ngSumit)="onSubmit()">
    <div class="login" align="center">
    <div><h2>MVP INFOTECH PVT. LTD.</h2>
    <div>
    <label for="email">Email ID :  <input type ="text" class="box" placeholder="Email"></label>
    <p>Password : <input type="password" class="box" placeholder="Password"></p>
    </div>
    </div>
    </div>
    <div><input id="submit" type="submit" value="SUBMIT"></div>
    </form>
</body>
</html>
