import { Component, OnInit } from '@angular/core';
import { Employee } from '../employee';
import { EmployeeService } from '../employee.service';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-update-employee',
  templateUrl: './update-employee.component.html',
  styleUrls: ['./update-employee.component.css']
})

export class UpdateEmployeeComponent implements OnInit {
  id!:number;
  employee:Employee = new Employee();

  constructor( private employeeService:EmployeeService,
    private route: ActivatedRoute,
    private router:Router) {}
  ngOnInit():void{
    this.id=this.route.snapshot.params['id'];
    this.employeeService.updateEmployeeById(this.id).subscribe(data=>{
      this.employee = data;
    });

  }
    onSubmit(){
      this.employeeService.updateEmployee(this.id,this.employee).subscribe(data=>{
        this.goToEmployeeList();
      })

    }
    goToEmployeeList(){
      this.router.navigate(['/employee']);
    }
  }
    <div class="col-md-4 offset-md-3">
    <h2>Update Employee details</h2>
    <form (ngSubmit) ="onSubmit()">
        <div class="form-group">
            <label>Emp Name</label>
            <input type="text" class="form-control" id="empname"
            [(ngModel)] = "employee.empName"  name = "empName">
        </div>
        <div class="form-group">
            <label>Employee Designation </label>
            <input type="text" class="form-control" id="empDesignation"
            [(ngModel)] = "employee.designation"  name = "empDesig">
        </div>
        <div class="form-group">
            <label>Employee Location </label>
            <input type="text" class="form-control" id="empLocation"
            [(ngModel)] = "employee.location"  name = "empLocatuom">
        </div>
        <div class="form-group">
            <label>Employee Email </label>
            <input type="text" class="form-control" id="empEmail"
            [(ngModel)] = "employee.email"  name = "empEmail">
        </div>
        <div class="form-group">
            <label>Employee Address</label>
            <input type="text" class="form-control" id="empAddress"
            [(ngModel)] = "employee.address"  name = "empAddress">
        </div>

        <button class="btn btn-success" type="submit" >Update</button>
      <!-- <div class="btn">
       <button type="submit" form="form1" value="Submit">Submit</button>
        </div>-->

      </form>
    </div>
