import { Component } from '@angular/core';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

}
<html>
<head> <title> Login to MVP Infotec Pvt Ltd.</title>
<style>
    body , .login{
        align-items: center;
        border: 0px;
        background-color: rgb(226, 171, 69);
    }
.login{
    border: 0px solid; 
    margin-left: 30%; 
    margin-right: 30%; 
    background-color: rgb(228, 157, 25); 
    padding: 20px; 
    font-size: larger;
}
.box{
     font-family:'Courier New', Courier, monospace;
     letter-spacing: 1px; 
}
.button{
    background-color: aqua;
     border-radius: 6px; 
     font-style: oblique; 
     font-weight: bolder; 
     color: red; 
}
#submit , #newuser{
    float: left;
    background-color: rgb(228, 157, 25);
     font-style: oblique; 
     font-weight: bolder;
     margin-left: 30%;
     width: 20%;
     height: 5%;
     border:0px solid;
}
#submit:hover, #newuser:hover{
    color: green;
    background-color: orange;
}
#newuser{
    margin-left: 0%;
}
#forgot{
    font-size: small;
    text-align: right;
    margin-left: 40%;
}
</style>
</head>
<body>
    <div class="login" align="center">
    <div><h2>MVP INFOTECH PVT. LTD.</h2>
    <div>
    <p>Username :  <input type ="text" class="box" placeholder="Username"></p>
    <p>Password : <input type="password" class="box" placeholder="Password"></p>
    <span id="forgot"><a href="#">Forget Password?</a></span>
    </div>
    </div>
    </div>
    <div><input id="submit" type="submit" value="SUBMIT"></div>
    <div><input id="newuser" type="button" value="New User?"></div>
</body>
</html>
