import { Component, OnInit } from '@angular/core';
import { Claims } from '../claims';
import { ClaimsService } from '../claims.service';
import{FormBuilder, FormGroup} from '@angular/forms';

@Component({
  selector: 'app-employee-claim-page',
  templateUrl: './employee-claim-page.component.html',
  styleUrls: ['./employee-claim-page.component.css']
})
export class EmployeeClaimPageComponent implements OnInit {
[x: string]: any;

  claims:Claims = new Claims();
claimsform!: FormGroup<any>;
employeeName: any;
name: any;
public selectedFile:any;
  constructor(  private claimService:ClaimsService,private formBuilder:FormBuilder) {
  }


  ngOnInit() : void{
  }
  saveClaim(){
    const uploadData = new FormData();
    uploadData.append('myFile',this.selectedFile,this.selectedFile.name);

    this.claimService.createClaim(this.claims).subscribe(data =>{
      console.log(data);
    });
  }
  onSubmit() {
   console.log(this.claims);
    this.saveClaim();
    console.log("details added successfully")
   }

  }

<!DOCTYPE html>
<html lang="en">
<head>
    <title>Employee Claims</title>

</head>
<body >
    <form  (ngSubmit)="onSubmit()">
    <h3 align="center">Employee Claims Form</h3><br>
        <table align="center">
            <tr>
                <td align="right"> <strong> Employee Name:  </strong></td>
                <td><input type="text"  id="employeeName" required (ngModel)="claims.name" name="employeeName" required="">  
                </td><br>
            </tr><br>
            <tr>
                <td align="right"><strong>Claim Amount:  </strong></td>
                <td ><input type="number" id="Amount" required (ngModel)="claims.claimType" name="claimType" required="">
                </td>
            </tr><br>
            <tr>
                <td align="right"><strong>Claim Type:  </strong> </td>
                    <td>
                    <select id="ddlclaimType"  required (ngModel)="claims.amount" name="amount" >   
                        <option>--select-- </option>
                        <option>food</option>
                        <option>medical</option>
                        <option>travel</option>
                        <option>home</option>
                        </select>
                </td>  
            </tr><br>
            <tr>
                <td align="right"><strong>Discription:  </strong></td> 
                <td > <textarea id="claimDescription" rows="4" cols="40" required (ngModel)="claims.description" name="description">             
                </textarea>
                </td>
            </tr><br>
            <tr>
                <td align="right"><strong>Receipt:  </strong></td>
                <td><input type="file" id="Receipt" accept="image/*" required (ngModel) ="claims.pic" name="pic">
                    </td><br>
            </tr><br>
          <tr>
            <td align="center"> </td>
                <td>
                    <button  class="btn btn-success" type="submit">Submit Claim</button>
                </td>
          </tr>
        </table>
    </form>
</body>
</html>
