import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { User } from '../user';
import { LoginuserService } from '../loginuser.service';
import { Router } from '@angular/router';
import { AuthGuard } from '../services/auth.guard';
import { map, of } from 'rxjs';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit{

  userInfo = new BehaviorSubject(null);
  jwtHelper = new JwtHelperService();

  constructor(private loginUserService : LoginuserService, private router: Router, private authGuard: AuthGuard) { }
  user:User = new User();
  loginForm!: FormGroup;
  ngOnInit(){
    this.loginForm = new FormGroup({
    email: new FormControl('',[Validators.required,Validators.email]),
    password: new FormControl('',Validators.required)
  });
}
onSubmit(){this.loginUserService.loginUser(this.user).subscribe(data => {
  if(this.user &&
    this.user.userId &&
    this.user.password){
     const sampleJWT = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VybmFtZSI6IlRlc3QiLCJzdWIiOjIsImlhdCI6MTYwNDMwOTc0OSwiZXhwIjoxNjA0MzA5ODA5fQ.jHez9kegJ7GT1AO5A2fQp6Dg9A6PBmeiDW1YPaCQoYs";

     return of(sampleJWT).pipe(
     map((token) => {
    if(!token){
      return false;
    }
    localStorage.setItem("access_token", token);
    const decodedUser = this.jwtHelper.decodeToken(token);
    this.userInfo.next(decodedUser);
    return true;
     }));
    }
    return of(false);
    this.router.navigateByUrl('home')
},(error)=>alert('Wrong Details'));

}
}
